export const REGIONS = Object.keys(REGIONS_WITH_CITIES);

export type Region = typeof REGIONS[number];

// –•–µ–ª–ø–µ—Ä—ã
export function getCategoryName(categoryId: string): string {
  return CATEGORIES[categoryId as CategoryId]?.name || categoryId;
}

export function getSubcategoryName(categoryId: string, subcategoryId: string): string {
  const category = CATEGORIES[categoryId as CategoryId];
  if (!category) return subcategoryId;
  const sub = category.subcategories.find(s => s.id === subcategoryId);
  return sub?.name || subcategoryId;
}

export function getAllSubcategories() {
  return Object.entries(CATEGORIES).flatMap(([catId, cat]) =>
    cat.subcategories.map(sub => ({
      categoryId: catId,
      categoryName: cat.name,
      subcategoryId: sub.id,
      subcategoryName: sub.name,
      icon: cat.icon,
    }))
  );
}

export function getCitiesByRegion(region: string): string[] {
  return REGIONS_WITH_CITIES[region] || [];
}

export function getRegionByCity(city: string): string | null {
  for (const [region, cities] of Object.entries(REGIONS_WITH_CITIES)) {
    if (cities.includes(city)) {
      return region;
    }
  }
  return null;
}

